package Assignments;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 23;
		int b = 32;
		int c;
		c  = a*b;
		System.out.println(+c);
		if(c%2 == 0)
			System.out.println("the muliple of a and b is even");
		else
			System.out.println("the muliple of a and b is odd");
	}

}
